import React,{useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import "./navbar.css";
import Logo from "../Queue_Logo.png"

function navBar(){
  const [selectedItem, setSelectedItem] = useState(null);

  const handleClick = (index) => {
    setSelectedItem(index);
  };
  return (
    <>
        <nav className = "navBar">
        
        <img src={Logo} alt="Icon" className="icon"/>
        <ul className="nav-menu">
            <Link to = '/' className='Underline'>
                <li className={selectedItem === 0 ? 'selected' : ''} onClick={() => handleClick(0)}>Home</li>
            </Link>
            <Link to = '/Membership' className='Underline'>
                <li className={selectedItem === 1 ? 'selected' : ''} onClick={() => handleClick(1)}>Membership</li>
            </Link>
            <li>Booking</li>
            <li>Events</li>
            <li>Forum</li>
            <li>Resources</li>
            <li>Gallery</li>
            
        </ul>
        </nav>
    </>
  );
}

export default navBar;
