import React, { useState } from 'react';
import './Booking.css';
import { Hero } from '../Hero';

function Booking() {
  const [selectedButton, setSelectedButton] = useState(null);
  const [selectedButtonT, setSelectedButtonT] = useState(null);
  const [selectedButtonTi, setSelectedButtonTi] = useState(null);

  const handleButtonClick = (buttonId) => {
    setSelectedButton((prevButton) => (prevButton === buttonId ? null : buttonId));
  };

  const handleButtonClickT = (buttonId) => {
    setSelectedButtonT((prevButton) => (prevButton === buttonId ? null : buttonId));
  };

  const handleButtonClickTi = (buttonId) => {
    setSelectedButtonTi((prevButton) => (prevButton === buttonId ? null : buttonId));
  };

  const getDayName = (dayIndex) => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    return days[dayIndex];
  };

  const renderCalendarCells = () => {
    const currentDate = new Date();
    const currentDay = currentDate.getDay();
    const currentMonth = currentDate.getMonth();
    const currentYear = currentDate.getFullYear();
    const calendarCells = [];

    for (let i = 0; i < 7; i++) {
      const date = currentDate.getDate() + i;
      const dayIndex = (i + currentDay) % 7;
      const dayName = getDayName(dayIndex);

      if (date <= daysInMonth(currentMonth, currentYear)) {
        calendarCells.push(
          <td key={i}>
            <button
              id={i}
              className={selectedButton === i ? 'selected' : ''}
              onClick={() => handleButtonClick(i)}
            >
              {date}
            </button>
          </td>
        );
      }
    }

    return calendarCells;
  };

  const daysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  return (
    <>
      <Hero MainTextOut="Book a" MainTextOth="Session" MainTextSize="100px" MainTextMarginTop="20px" />
      <table className="Calendar">
        <tbody>
          <tr>
            {renderCalendarCells()}
          </tr>
        </tbody>
      </table>
      <table className="Tables">
        <tbody>
          <tr>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickT('1T')}
                id="1T"
                className={selectedButtonT === '1T' ? 'selected' : ''}
                value="Table 1"
              />
            </td>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickT('2T')}
                id="2T"
                className={selectedButtonT === '2T' ? 'selected' : ''}
                value="Table 2"
              />
            </td>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickT('3T')}
                id="3T"
                className={selectedButtonT === '3T' ? 'selected' : ''}
                value="Table 3"
              />
            </td>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickT('4T')}
                id="4T"
                className={selectedButtonT === '4T' ? 'selected' : ''}
                value="Table 4"
              />
            </td>
          </tr>
        </tbody>
      </table>
      <table className="TimeSlot">
        <tbody>
          <tr>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickTi('4B')}
                id="4B"
                className={selectedButtonTi === '4B' ? 'selected' : ''}
                value="16:00"
              />
            </td>
            <td>
              <input
                id="8B"
                onClick={() => handleButtonClickTi('8B')}
                type="button"
                className={selectedButtonTi === '8B' ? 'selected' : ''}
                value="20:00"
              />
            </td>
          </tr>
          <tr>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickTi('5B')}
                id="5B"
                className={selectedButtonTi === '5B' ? 'selected' : ''}
                value="17:00"
              />
            </td>
            <td>
              <input
                id="9B"
                onClick={() => handleButtonClickTi('9B')}
                type="button"
                className={selectedButtonTi === '9B' ? 'selected' : ''}
                value="21:00"
              />
            </td>
          </tr>
          <tr>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickTi('6B')}
                id="6B"
                className={selectedButtonTi === '6B' ? 'selected' : ''}
                value="18:00"
              />
            </td>
            <td>
              <input
                id="10B"
                onClick={() => handleButtonClickTi('10B')}
                type="button"
                className={selectedButtonTi === '10B' ? 'selected' : ''}
                value="22:00"
              />
            </td>
          </tr>
          <tr>
            <td>
              <input
                type="button"
                onClick={() => handleButtonClickTi('7B')}
                id="7B"
                className={selectedButtonTi === '7B' ? 'selected' : ''}
                value="19:00"
              />
            </td>
            <td>
              <input
                id="11B"
                onClick={() => handleButtonClickTi('11B')}
                type="button"
                className={selectedButtonTi === '11B' ? 'selected' : ''}
                value="23:00"
              />
            </td>
          </tr>
        </tbody>
      </table>
      <button className="ConfirmBook">Book!</button>
    </>
  );
}

export default Booking;
