import React, { useState } from 'react';
import "./Booking.css";

function Booking() {
  const [selectedButton, setSelectedButton] = useState(null);

  const handleButtonClick = (buttonId) => {
    setSelectedButton(buttonId);
  };

  return (
    <>
      <table className="Calendar"></table>
      <table className="Tables">
        <tr>
          <td><input type='button' onClick={() => handleButtonClick('1T')} id="1T" value='Table 1' /></td>
          <td><input type='button' onClick={() => handleButtonClick('2T')} id="2T" value='Table 2' /></td>
          <td><input type='button' onClick={() => handleButtonClick('3T')} id="3T" value='Table 3' /></td>
          <td><input type='button' onClick={() => handleButtonClick('4T')} id="4T" value='Table 4' /></td>
        </tr>
      </table>
      <table className="TimeSlot">
        <tr>
          <td><input type='button' onClick={() => handleButtonClick('4B')} id="4B" value='16:00' /></td>
          <td><input id="8B" onClick={() => handleButtonClick('8B')} type='button' value='20:00' /></td>
        </tr>
        <tr>
          <td><input type='button' onClick={() => handleButtonClick('5B')} id="5B" value='17:00' /></td>
          <td><input id="9B" onClick={() => handleButtonClick('9B')} type='button' value='21:00' /></td>
        </tr>
        <tr>
          <td><input type='button' onClick={() => handleButtonClick('6B')} id="6B" value='18:00' /></td>
          <td><input id="10B" onClick={() => handleButtonClick('10B')} type='button' value='22:00' /></td>
        </tr>
        <tr>
          <td><input type='button' onClick={() => handleButtonClick('7B')} id="7B" value='19:00' /></td>
          <td><input id="11B" onClick={() => handleButtonClick('11B')} type='button' value='23:00' /></td>
        </tr>
      </table>
      <button className="ConfirmBook">Book!</button>
      <script>
        {`
          const changeColor = (buttonId) => {
            const button = document.getElementById(buttonId);
            button.style.backgroundColor = "#30D5C8";
            button.style.color = "black";
            button.style.textShadow = "1px 1px 2px black";
          }
        `}
      </script>
    </>
  );
}

export default Booking;
